/************************************************
*  函数名称：LCD_PORT_init
*  功能说明：240*320的SPI LCD引脚初始化
*  参数说明：无
*  函数返回：无0
*  修改时间：2014-1-14    已经测试
*************************************************/
void LCD_PORT_init()
{
    gpio_init(PORTE, 26, GPO, HIGH);//cs
    gpio_init(PORTE, 27, GPO, HIGH);//rst
    gpio_init(PORTE, 28, GPO, HIGH);//dc
    gpio_init(PORTE, 25, GPO, HIGH);//sdi
    gpio_init(PORTE, 24, GPO, HIGH);//scl
}

/************************************************
*  函数名称：LCD_Reset
*  功能说明：240*320的SPI LCD复位
*  参数说明：无
*  函数返回：无
*  修改时间：2014-1-14    已经测试
*************************************************/
void LCD_Reset()
{
    reset = 0;
    delayms(20);
    reset = 1;
    delayms(20);
}


/************************************************
*  函数名称：LCD_Reset
*  功能说明：240*320的SPI LCD模块初始化
*  参数说明：direction取TRUE和FALSE，屏幕正反之分
*  函数返回：无
*  修改时间：2014-1-14    已经测试
*************************************************/
void LCD_init(unsigned char direction)
{
    //先端口输入输出配置
    LCD_PORT_init();
    
    reset=0;
    delayms(20);
    reset=1;
    delayms(20);
    //------------------------------------------------------------------//
    //-------------------Software Reset-------------------------------//
    write_command(0xCB);
    write_data(0x39);
    write_data(0x2C);
    write_data(0x00);
    write_data(0x34);
    write_data(0x02);

    write_command(0xCF);
    write_data(0x00);
    write_data(0XC1);
    write_data(0X30);

    write_command(0xE8);
    write_data(0x85);
    write_data(0x00);
    write_data(0x78);

    write_command(0xEA);
    write_data(0x00);
    write_data(0x00);

    write_command(0xED);
    write_data(0x64);
    write_data(0x03);
    write_data(0X12);
    write_data(0X81);

    write_command(0xF7);
    write_data(0x20);

    write_command(0xC0);    //Power control
    write_data(0x23);   //VRH[5:0]

    write_command(0xC1);    //Power control
    write_data(0x10);   //SAP[2:0];BT[3:0]

    write_command(0xC5);    //VCM control
    write_data(0x3e); //对比度调节
    write_data(0x28);

    write_command(0xC7);    //VCM control2
    write_data(0x86);  //--

    write_command(0x36);    // Memory Access Control
    if(direction)
        write_data(0xE8); //C8	   //48 68竖屏//28 E8 横屏
    else
        write_data(0x28);//0b00101000


    write_command(0x3A);
    write_data(0x55);

    write_command(0xB1);
    write_data(0x00);
    write_data(0x18);

    write_command(0xB6);    // Display Function Control
    write_data(0x08);
    write_data(0x82);
    write_data(0x27);

    write_command(0xF2);    // 3Gamma Function Disable
    write_data(0x00);

    write_command(0x26);    //Gamma curve selected
    write_data(0x01);

    write_command(0xE0);    //Set Gamma
    write_data(0x0F);
    write_data(0x31);
    write_data(0x2B);
    write_data(0x0C);
    write_data(0x0E);
    write_data(0x08);
    write_data(0x4E);
    write_data(0xF1);
    write_data(0x37);
    write_data(0x07);
    write_data(0x10);
    write_data(0x03);
    write_data(0x0E);
    write_data(0x09);
    write_data(0x00);

    write_command(0XE1);    //Set Gamma
    write_data(0x00);
    write_data(0x0E);
    write_data(0x14);
    write_data(0x03);
    write_data(0x11);
    write_data(0x07);
    write_data(0x31);
    write_data(0xC1);
    write_data(0x48);
    write_data(0x08);
    write_data(0x0F);
    write_data(0x0C);
    write_data(0x31);
    write_data(0x36);
    write_data(0x0F);

    write_command(0x11);    //Exit Sleep
    delayms(20);

    write_command(0x29);    //Display on
    write_command(0x2c);
}

/************************************************
*  函数名称：write_command
*  功能说明：LCD写指令函数
*  参数说明：c为指令
*  函数返回：无
*  修改时间：2014-1-14    已经测试
*************************************************/
void  write_command(unsigned char  c)
{
    cs=0;
    dcx=0;
    sdi=(GET_BITFIELD(c))->bit7;scl=0;scl=1;
    sdi=(GET_BITFIELD(c))->bit6;scl=0;scl=1;
    sdi=(GET_BITFIELD(c))->bit5;scl=0;scl=1;
    sdi=(GET_BITFIELD(c))->bit4;scl=0;scl=1;
    sdi=(GET_BITFIELD(c))->bit3;scl=0;scl=1;
    sdi=(GET_BITFIELD(c))->bit2;scl=0;scl=1;
    sdi=(GET_BITFIELD(c))->bit1;scl=0;scl=1;
    sdi=(GET_BITFIELD(c))->bit0;scl=0;scl=1;
    cs=1;
}

/************************************************
*  函数名称：write_data
*  功能说明：LCD写数据函数
*  参数说明：d为数据，为一个BYTE的数据
*  函数返回：无
*  修改时间：2014-1-14    已经测试
*************************************************/
void  write_data(unsigned char  d)
{
    cs=0;
    dcx=1;
    sdi=(GET_BITFIELD(d))->bit7;scl=0;scl=1;
    sdi=(GET_BITFIELD(d))->bit6;scl=0;scl=1;
    sdi=(GET_BITFIELD(d))->bit5;scl=0;scl=1;
    sdi=(GET_BITFIELD(d))->bit4;scl=0;scl=1;
    sdi=(GET_BITFIELD(d))->bit3;scl=0;scl=1;
    sdi=(GET_BITFIELD(d))->bit2;scl=0;scl=1;
    sdi=(GET_BITFIELD(d))->bit1;scl=0;scl=1;
    sdi=(GET_BITFIELD(d))->bit0;scl=0;scl=1;
    cs=1;
}

/************************************************
*  函数名称：write_word
*  功能说明：LCD写数据函数
*  参数说明：dat为数据，为两个BYTE的数据
*  函数返回：无
*  修改时间：2014-1-14    已经测试
*************************************************/
void write_word(unsigned int dat)
{
    write_data(dat>>8);
    write_data(dat);
}

/************************************************
*  函数名称：RamAdressSet
*  功能说明：LCD的设置RAM地址
*  参数说明：无
*  函数返回：无
*  修改时间：2014-1-14    已经测试
*************************************************/
void  RamAdressSet()
{
    write_command(0x2A);
    write_data(0x00);
    write_data(0x00);
    write_data(0x00);
    write_data(0xaf);

    write_command(0x2B);
    write_data(0x00);
    write_data(0x00);
    write_data(0x00);
    write_data(0xdb);		
}


/************************************************
*  函数名称：LCD_SetPos
*  功能说明：LCD的设置写入屏幕的地址
*  参数说明：横坐标X0~X1，纵坐标为Y0~Y1
*  函数返回：无
*  修改时间：2014-1-14    已经测试
*************************************************/
void LCD_SetPos(unsigned int x0,unsigned int x1,unsigned int y0,unsigned int y1)
{
    write_command(0x2A);
    write_data(x0>>8);
    write_data(x0);
    write_data(x1>>8);
    write_data(x1);

    write_command(0x2B);
    write_data(y0>>8);
    write_data(y0);
    write_data(y1>>8);
    write_data(y1);
    write_command(0x2c);
}

