    {
       s[count++] = '-';
       pnum = (-1) * pnum;
    }
    if(pnum < 10)
    {
        s[count++]=pnum+'0';
    }
    else if((10<=pnum)&&(pnum<100))
    {
        s[count++]=pnum/10+'0';
        s[count++]=pnum%10+'0';
    }
    else if(pnum<=999)
    {
        s[count++]=pnum/100+'0';
        s[count++]=pnum/10%10+'0';
        s[count++]=pnum%10+'0';
    }
